import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';
import { Grid } from '@material-ui/core';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  grid: {
    display: 'flex',
    direction: 'row',
    flexWrap: 'wrap',
    gap: 10,
  },
});

export default function Classes() {
  const classes = useStyles();
  const [list, setList] = useState([]);
  useEffect(() => {
    axios
      .get('http://localhost:3000/users')
      .then((res) => {
        setList(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <Grid className={classes.grid}>
      {list &&
        list.map((ele, index) => (
          <Card key={ele._id} className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image='https://scontent.fsgn5-6.fna.fbcdn.net/v/t1.6435-9/233431693_3120326968189204_2503134200782106609_n.jpg?_nc_cat=106&ccb=1-5&_nc_sid=8bfeb9&_nc_ohc=sMPS3fjWjfUAX8dChd5&_nc_ht=scontent.fsgn5-6.fna&oh=172e92aa10844bcda4164717d3b03bb6&oe=61A82582'
                title='Contemplative Reptile'
              />
              <CardContent>
                <Typography
                  gutterBottom
                  variant='h5'
                  component='h2'
                ></Typography>
                <Typography variant='body2' color='textSecondary' component='p'>
                  {list}
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size='small' color='primary'>
                Share
              </Button>
              <Button size='small' color='primary'>
                Learn More
              </Button>
            </CardActions>
          </Card>
        ))}
    </Grid>
  );
}
