import { Axios } from 'axios';
import React, { useState } from 'react';

export default function AddClass() {
  const url = 'http://localhost:3000/users';

  const [data, setData] = useState({
    name: '',
    age: '',
    email: '',
  });

  function submit(e) {
    e.preventDefault();
    Axios.post(url, {
      name: data.name,
      age: parseInt(data.age),
      email: data.email,
    }).then((res) => console.log(res.data));
  }
  function handle(e) {
    const newData = { ...data };
    newData[e.target.id] = e.target.value;
    setData(newData);
  }

  return (
    <div>
      <form onSubmit={(e) => submit(e)}>
        <input
          onChange={(e) => handle(e)}
          id='name'
          value={data.name}
          placeholder='name'
          type='text'
        ></input>
        <input
          onChange={(e) => handle(e)}
          id='email'
          value={data.email}
          placeholder='email'
          type='text'
        ></input>
        <input
          onChange={(e) => handle(e)}
          id='age'
          value={data.age}
          placeholder='age'
          type='number'
        ></input>
      </form>
    </div>
  );
}
